import { useEffect, useState } from "react";

const SimpleInput = (props) => {
  const [enteredValue, setEnteredValue] = useState("");
  const [isNameFocus, setIsNameFocus] = useState(false);
  // const [isFormValid, setIsFormValid] = useState(false);

  const isNameValidNow = enteredValue.trim() !== "";
  const isInputValid = !isNameValidNow && isNameFocus;
  const isFormValid = isNameValidNow;

  let nameInputClasses = isFormValid ? "form-control invalid" : "form-control ";

  // useEffect(() => {
  //   if (isNameValidNow) {
  //     setIsFormValid(true);
  //   } else {
  //     setIsFormValid(false);
  //   }
  // }, [isNameValidNow]);

  const nameHandlerFunction = (event) => {
    setEnteredValue(event.target.value);
  };

  const NameBlurHandler = () => {
    setIsNameFocus(true);
  };

  const FormHandlerFunction = (e) => {
    e.preventDefault();
    setIsNameFocus(true);
    if (!isNameValidNow) {
      return;
    }
    console.log(enteredValue);
    setEnteredValue("");
    setIsNameFocus(false);
  };

  return (
    <form onSubmit={FormHandlerFunction}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameHandlerFunction}
          onBlur={NameBlurHandler}
        />
        {isFormValid && <p>WRONG value</p>}
      </div>
      <div className="form-actions">
        <button disabled={!isFormValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
