import { useState } from "react";

const SimpleInput = (props) => {
  const [enteredValue, setEnteredValue] = useState("");
  const [isNameValid, setIsNameValid] = useState(false);
  const [isNameFocus, setIsNameFocus] = useState(false);

  const isInputValid = !isNameValid && isNameFocus;

  const nameHandlerFunction = (event) => {
    setEnteredValue(event.target.value);

    if (event.target.value.trim() !== "") {
      setEnteredValue(true);
    }
  };

  const FormHandlerFunction = (e) => {
    e.preventDefault();
    setIsNameFocus(true);
    if (enteredValue.trim() === "") {
      setIsNameValid(false);
      return;
    }
    setIsNameValid(true);
    console.log(enteredValue);
  };

  const nameInputClasses = isInputValid
    ? "form-control invalid"
    : "form-control ";

  const NameBlurHandler = () => {
    setIsNameFocus(true);
    if (enteredValue.trim() === "") {
      setIsNameValid(false);
      return;
    }
  };

  return (
    <form onSubmit={FormHandlerFunction}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameHandlerFunction}
          onBlur={NameBlurHandler}
        />
        {isInputValid && <p>WRONG value</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
