import useInput from "../hooks/use-input";

const BasicForm = (props) => {
  const NameCheck = (inComingValue) => {
    return inComingValue.trim() !== "";
  };

  const {
    enteredValue: enteredFirstName,
    isValid: isValidFirstName,
    isInputValid: isFirstNameInputValid,
  } = useInput(NameCheck);

  const FirstNameCss = isFirstNameInputValid
    ? "form-control invalid"
    : "form-control";

  const isButtonActive = isValidFirstName;
  return (
    <form>
      <div className="control-group">
        <div className={FirstNameCss}>
          <label htmlFor="name">First Name</label>
          <input type="text" id="name" value={enteredFirstName} />
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button disabled={isButtonActive}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
