import { createSlice } from "@reduxjs/toolkit";
import { uiActions } from "./ui-slice";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    totalQuantity: 0,
  },
  reducers: {
    replaceCart(state, action) {
      state.totalQuantity = action.payload.totalQuantity;
      state.items = action.payload.items;
    },
    addItemToCart(state, action) {
      const newItem = action.payload;
      const existingItem = state.items.find((item) => item.id === newItem.id);
      state.totalQuantity++;
      if (!existingItem) {
        state.items.push({
          id: newItem.id,
          price: newItem.price,
          quantity: 1,
          totalPrice: newItem.price,
          name: newItem.title,
        });
      } else {
        existingItem.quantity++;
        existingItem.totalPrice = existingItem.totalPrice + newItem.price;
      }
    },
    removeItemFromCart(state, action) {
      const id = action.payload;
      const existingItem = state.items.find((item) => item.id === id);
      state.totalQuantity--;
      if (existingItem.quantity === 1) {
        state.items = state.items.filter((item) => item.id !== id);
      } else {
        existingItem.quantity--;
      }
    },
  },
});

export const sendData = (cart) => {
  return async () => {
    dispatch(
      uiActions.notificationHandler({
        status: "pending",
        title: "pending...",
        message: "loading the fetch procceses",
      })
    );

    const fetchData = async () => {
      const response = await fetch(
        "https://http-request-1cf75-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (!response.ok) {
        console.log("erorr wauttttt");
        throw new Error("failed the this proccess unfortunatlyy");
      }
    };

    try {
      fetchData();
      dispatch(
        uiActions.notificationHandler({
          status: "succes",
          title: "succesfully...",
          message: "the processes has been complete succesfully",
        })
      );
    } catch (error) {
      dispatch(
        uiActions.notificationHandler({
          status: "error",
          title: "error...",
          message: "something went wrong",
        })
      );
    }
  };
};

export const cartActions = cartSlice.actions;

export default cartSlice;
