import { useDispatch, useSelector } from "react-redux";

import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useEffect } from "react";
import { uiActions } from "./store/ui-slice";

function App() {
  const showCart = useSelector((state) => state.ui.cartIsVisible);
  const cart = useSelector((state) => state.cart);
  dispatch = useDispatch();
  const notitication = useSelector((state) => state.ui.notitication);

  useEffect(() => {
    const HandlingFetch = () => {
      dispatch(
        uiActions.notificationHandler({
          status: "pending",
          title: "pending...",
          message: "loading the fetch procceses",
        })
      );
      const response = fetch(
        "https://http-request-1cf75-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (response.ok) {
        dispatch(
          uiActions.notificationHandler({
            status: "succes",
            title: "succesfully...",
            message: "the processes has been complete succesfully",
          })
        );
      }
    };
    HandlingFetch().catch((e) => {
      dispatch(
        uiActions.notificationHandler({
          status: "error",
          title: "error...",
          message: "something went wrong",
        })
      );
    });
  }, [cart]);

  return (
    <Layout>
      <Notification status />
      {showCart && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
