import { useDispatch, useSelector } from "react-redux";

import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useEffect } from "react";
import { uiActions } from "./store/ui-slice";
import Notification from "./components/UI/Notification";

let isInitial = true;

function App() {
  const showCart = useSelector((state) => state.ui.cartIsVisible);
  const cart = useSelector((state) => state.cart);
  const dispatch = useDispatch();
  const notification = useSelector((state) => state.ui.notification);

  useEffect(() => {
    const HandlingFetch = async () => {
      dispatch(
        uiActions.notificationHandler({
          status: "pending",
          title: "pending...",
          message: "loading the fetch procceses",
        })
      );
      const response = await fetch(
        "https://http-request-1cf75-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (!response.ok) {
        console.log("erorr wauttttt");
        throw new Error("failed the this proccess unfortunatlyy");
      }
      dispatch(
        uiActions.notificationHandler({
          status: "succes",
          title: "succesfully...",
          message: "the processes has been complete succesfully",
        })
      );
    };
    if (isInitial) {
      isInitial = false;
      return;
    }
    HandlingFetch().catch((e) => {
      dispatch(
        uiActions.notificationHandler({
          status: "error",
          title: "error...",
          message: "something went wrong",
        })
      );
    });
  }, [cart, dispatch]);
  console.log("status", notification);
  return (
    <Layout>
      <Notification
        status={notification.status}
        title={notification.title}
        message={notification.message}
      />
      {showCart && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
