import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  cart: [],
  totalAmount: 0,
  isActive: false,
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addItem: (state, action) => {
      console.log("trigereddd");
      console.log("action.payload:", action);
      state.cart.push(action.payload);
      state.totalAmount = state.totalAmount + action.payload.price;
    },
    increaseProduct: (state, action) => {
      state.totalAmount = state.totalAmount + action.payload.price;
      const index = state.cart.findIndex(
        (item) => item.title === action.payload.title
      );
      const uptadedProduct = {
        ...state.cart[index],
        quantity: state.cart[index].quantity + 1,
      };
      state.cart[index] = uptadedProduct;
    },
    showCartHandler: (state) => {
      state.isActive = !state.isActive;
    },
  },
});

export const { addItem, increaseProduct, showCartHandler } = cartSlice.actions;
export default cartSlice.reducer;
