import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  cart: [{ title: "were you succesfull", quantity: 4, total: 20, price: 5 }],

  totalAmount: 0,
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addItem: () => {
      initialState.cart.push({
        title: "Worked great",
        quantity: 4,
        total: 20,
        price: 5,
      });
    },
  },
});

export const { addItem } = cartSlice.actions;
export default cartSlice.reducer;
