import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  cart: [
    { title: "myFirst Products", quantity: 4, total: 20, price: 5 },
    { title: "we fdfdf", quantity: 4, total: 20, price: 5 },
  ],

  totalAmount: 0,
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addItem: (item) => {
      initialState.cart.push(item);
    },
  },
});

export const { addItem } = cartSlice.actions;
export default cartSlice.reducer;
