import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  cart: [{ title: "were you succesfull", quantity: 4, total: 20, price: 5 }],
  totalAmount: 5,
  isActive: true,
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addItem: (state, action) => {
      console.log("trigereddd");
      console.log("action.payload:", action);
      state.cart.push(action.payload);
      state.totalAmount = state.totalAmount + action.payload.price;
    },
    increaseProduct: (state, action) => {
      state.totalAmount = state.totalAmount + action.payload;
    },
    showCartHandler: (state, action) => {
      state.isActive = action.payload;
    },
  },
});

export const { addItem, increaseProduct, showCartHandler } = cartSlice.actions;
export default cartSlice.reducer;
