import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import "./Expense.css";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expense = ({ expenses }) => {
  const [filteredYear, setFilteredYear] = useState("2020");
  const [temp, setTemp] = useState(expenses);

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  setTemp(expenses.filter((item) => item.date.getFullYear() === filteredYear));

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        />

        {temp.map((item) => {
          console.log(item.date.getFullYear());
          return (
            <ExpenseItem
              filteredYear={filteredYear}
              key={item.id}
              expenses={item}
            />
          );
        })}

        {/* 
      {/* <ExpenseItem expenses={expenses[0]} />
      <ExpenseItem expenses={expenses[1]} />
      <ExpenseItem expenses={expenses[2]} />
      <ExpenseItem expenses={expenses[3]} />
      {expenses.map((item) => {
        return console.log(item.title);
      })} */}
      </Card>
    </div>
  );
};

export default Expense;
