import React, { useCallback, useReducer } from "react";
import CartContext from "./cart-context";

//REDUCER FUNCTION
const reducer = (state, action) => {
  const updateTotalAmount = state.totalAmount + action.payload.amount;
  let updateItems;
  if (action.type === "ADD") {
    const existItemIndex = state.items.findIndex(
      (item) => item.id === action.payload.id
    );
    let existItem = state.items[existItemIndex];
    if (existItem) {
      const updateItem = {
        ...existItem,
        amount: existItem.amount + action.payload.amount,
      };
      updateItems = [...state.items];
      updateItems[existItemIndex] = updateItem;
    } else {
      updateItems = state.items.concat(action.payload);
    }
  }
  return updateItems;
};

const initialState = {
  items: [],
  totalAmount: 0,
};

const CartProvider = (props) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  const addItemToCartHandler = (item) => {
    dispatch({ type: "ADD", payload: item });
  };

  const removeItemFromCartHandler = (id) => {
    dispatch({ type: "REMOVE", payload: id });
  };

  const cartcontext = {
    items: state.items,
    totalAmount: state.totalAmount,
    addItem: addItemToCartHandler,
    removeItem: removeItemFromCartHandler,
  };

  return (
    <CartContext.Provider value={cartcontext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
