import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState();
  const fetchMoviHandler = () => {
    fetch("https://swapi.dev/api/films/")
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        const movieList = data.results.map((movieItem) => {
          return {
            id: episode_id,
            title: title,
            releaseDate: release_date,
            openingText: opening_crawl,
          };
        });
        setMovies(movieList);
      });
  };
  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviHandler}>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
