import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const fetchMoviHandler = async () => {
    console.log("1");
    setIsLoading(true);
    const response = await fetch("https://swapi.dev/api/films/");
    console.log("2");
    const data = await response.json();
    console.log("3");
    const movieList = data.results.map((movieItem) => {
      return {
        id: movieItem.episode_id,
        title: movieItem.title,
        openingText: movieItem.opening_crawl,
        releaseDate: movieItem.release_date,
      };
    });
    setMovies(movieList);
    setIsLoading(false);
  };
  console.log("4");
  console.log(isLoading);
  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && <MoviesList movies={movies} />}
        {isLoading && <p>loading...</p>}
      </section>
      {console.log("5")}
    </React.Fragment>
  );
}

export default App;
