import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  console.log("ekstra");

  const fetchMoviHandler = async () => {
    try {
      console.log("1");
      setIsLoading(true);
      console.log("wait or not");
      const response = await fetch("https://swapi.dev/api/films/");
      if (!response.ok) {
        throw new Error("somthing went wrong!");
      }
      console.log("2");
      const data = await response.json();
      console.log("3");
      const movieList = data.results.map((movieItem) => {
        return {
          id: movieItem.episode_id,
          title: movieItem.title,
          openingText: movieItem.opening_crawl,
          releaseDate: movieItem.release_date,
        };
      });
      setMovies(movieList);
      setIsLoading(false);
    } catch (error) {}
  };
  console.log("4");
  console.log(isLoading);

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && movies.length > 0 && <MoviesList movies={movies} />}
        {!isLoading && movies.length === 0 && <p>no found movies</p>}
        {isLoading && <p>loading...</p>}
      </section>
      {console.log("5")}
    </React.Fragment>
  );
}

export default App;
