import React, { useEffect, useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  console.log("ekstra");

  useEffect(() => {
    console.log("useefect worked");
    fetchMoviHandler();
  });

  const fetchMoviHandler = async () => {
    try {
      console.log("1");
      setIsLoading(true);
      setError(null);
      console.log("wait or not");
      const response = await fetch("https://swapi.dev/api/film/");
      if (!response.ok) {
        throw new Error("somthing went wrong!");
      }
      console.log("2");
      const data = await response.json();
      console.log("3");
      const movieList = data.results.map((movieItem) => {
        return {
          id: movieItem.episode_id,
          title: movieItem.title,
          openingText: movieItem.opening_crawl,
          releaseDate: movieItem.release_date,
        };
      });
      setMovies(movieList);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  };

  console.log("4");
  console.log(isLoading);

  let content = <p>no found movies</p>;

  if (movies.length > 0) {
    content = <MoviesList movies={movies} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>loading...</p>;
  }
  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviHandler}>Fetch Movies</button>
      </section>
      <section>{content}</section>
      {console.log("5")}
    </React.Fragment>
  );
}

export default App;
