import { useEffect, useState } from "react";

const useHttp = (initialValue) => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [data, setData] = useState([]);
  // const [createdTask, setCreatedTask] = useState;
  var createdTask;

  const httpTasks = async (taskText) => {
    setIsLoading(true);

    setError(null);
    try {
      const response = await fetch(
        "https://http-request-1cf75-default-rtdb.firebaseio.com//tasks.json",
        initialValue && {
          method: "POST",
          body: JSON.stringify({ text: taskText }),
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      if (!response.ok) {
        throw new Error("Request failed!");
      }

      const data = await response.json();
      if (initialValue) {
        const generatedId = data.name; // firebase-specific => "name" contains generated id
        createdTask = { id: generatedId, text: taskText };
      } else {
        const loadedTasks = [];

        for (const taskKey in data) {
          loadedTasks.push({ id: taskKey, text: data[taskKey].text });
        }
        setData(loadedTasks);
      }
    } catch (err) {
      setError(err.message || "Something went wrong!");
    }
    setIsLoading(false);
  };

  return { data, isLoading, error, httpTasks, createdTask };
};

export default useHttp;
