import { createStore } from "redux";

const counterReducer = (state = { count: 8 }, action) => {
  if (action.type === "increment") {
    return {
      state: state.count + 1,
    };
  }

  if (action.type === "decrement") {
    return {
      state: state.count - 1,
    };
  }
  return state;
};

const store = createStore(counterReducer);

export default store;
