import { createSlice, configureStore } from "@reduxjs/toolkit";

const initialAuthState = { isAuthenticated: true };

const authSlice = createSlice({
  name: "authntication",
  initialState: initialAuthState,
  reducers: {
    login(state) {
      state.isAuthenticated = true;
    },
    logout(state) {
      state.isAuthenticated = false;
    },
  },
});

const store = configureStore({
  reducer: {
    counter: counterSlice.reducer, /// this means is our values essenatially reducer means initialState and name so our values
    auth: authSlice.reducer,
  },
});
export const CounterActions = counterSlice.actions; // this means actually actions means our functions so reducer part
export const AuthActions = authSlice.actions;
export default store;
