import { createSlice, configureStore } from "@reduxjs/toolkit";

const initialCounterState = { count: 0, showCounter: true };

const counterSlice = createSlice({
  name: "counter",
  initialState: initialCounterState,
  reducers: {
    increment(state) {
      state.count++;
    },
    decrement(state) {
      state.count--;
    },
    incrementbyfive(state, action) {
      state.count = state.count + action.payload;
    },
    toggleHandler(state) {
      state.showCounter = !state.showCounter;
    },
  },
});

const initialAuthState = {};

const store = configureStore({
  reducer: counterSlice.reducer, /// this means is our values essenatially reducer means initialState and name so our values
});
export const CounterActions = counterSlice.actions; // this means actually actions means our functions so reducer part
export default store;
